import React, { useEffect, useState } from "react";

import "./App.css";
import spotifyIcon from "./images/Spotify_icon.svg";
import Thomas from "./images/Thomas.jpeg";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
  useLocation,
} from "react-router-dom";

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <header className={`header-container ${isVisible ? "visible" : ""}`}>
      <div className="left-header-container">
        <img className="spotify-icon" src={spotifyIcon} alt="icon" />
        <p>ArtistBlender</p>
      </div>
      <NavLink to="/about" className="about">
        About
      </NavLink>
    </header>
  );
};

const ArtistBlender = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <div className={`title-container ${isVisible ? "visible" : ""}`}>
      <h1 className="artistblender">ArtistBlender</h1>
      <p className="subtitle">
        Select your favorite artists and enjoy and their shuffled songs
      </p>
      <button className="sign-up">Start listening now!</button>
    </div>
  );
};

const Home = () => {
  return (
    <main className="app">
      <ArtistBlender />
    </main>
  );
};

const AppContent = () => {
  const location = useLocation();
  const hideHeader = location.pathname === "/about";
  return (
    <>
      {!hideHeader && <Header />}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
      </Routes>
    </>
  );
};

const App = () => {
  return (
    <Router>
      <main className="main">
        <AppContent />
      </main>
    </Router>
  );
};

const About = () => {
  return (
    <div className="about-container">
      <h1>About me</h1>
      <img className="Thomas" src={Thomas} alt="Thomas" />
    </div>
  );
};

export default App;
