import React, { useEffect, useState } from "react";

import "./App.css";
import spotifyIcon from "./images/Spotify_icon.svg";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
  useLocation,
  useNavigate,
} from "react-router-dom";

const Header = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <header className={`header-container ${isVisible ? "visible" : ""}`}>
      <div className="left-header-container">
        <img className="spotify-icon" src={spotifyIcon} alt="icon" />
        <p>ArtistBlender</p>
      </div>
      <NavLink to="/about" className="about">
        About
      </NavLink>
    </header>
  );
};

const ArtistBlender = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <div className={`title-container ${isVisible ? "visible" : ""}`}>
      <h1 className="artistblender">ArtistBlender</h1>
      <p className="subtitle">
        Select your favorite artists and enjoy and their shuffled songs
      </p>
      <button className="sign-up">Start listening now!</button>
    </div>
  );
};

const SignUp = () => {
  return (
    <div>
      <Header />
      <ArtistBlender />
    </div>
  );
};

const AboutHeader = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <header className={`about-header-container ${isVisible ? "visible" : ""}`}>
      <div className="left-header-container">
        <img className="spotify-icon" src={spotifyIcon} alt="icon" />
        <p>ArtistBlender</p>
      </div>
      <NavLink to="/about" className="about">
        About
      </NavLink>
    </header>
  );
};

const About = () => {
  return (
    <div className="about-page">
      <AboutHeader />
      <div className="about-container">
        <div className="about-info">About me</div>
      </div>
    </div>
  );
};

const AppContent = () => {
  return (
    <Routes>
      <Route path="/" element={<SignUp />} />
      <Route path="/about" element={<About />} />
    </Routes>
  );
};

const App = () => {
  return (
    <main className="app">
      <Router>
        <AppContent />
      </Router>
    </main>
  );
};

export default App;
